import React, { useState } from "react";

// Dummy Housekeeping Data
const housekeepingData = [
  {
    roomNumber: "101",
    status: "Cleaned",
    assignedTo: "John Doe",
    lastCleaned: "2024-12-15",
    maintenanceRequired: false,
    beforeImage: "data:image/jpeg;base64,",
    afterImage: "data:image/jpeg;base64,",
  },
  {
    roomNumber: "102",
    status: "In Progress",
    assignedTo: "Jane Smith",
    lastCleaned: "2024-12-16",
    maintenanceRequired: false,
    beforeImage: "data:image/jpeg;base64,",
    afterImage: "data:image/jpeg;base64,",
  },
  {
    roomNumber: "103",
    status: "Pending",
    assignedTo: "Alice Brown",
    lastCleaned: "2024-12-18",
    maintenanceRequired: true,
    beforeImage: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkQNYYX_MrSbgLJSn4_4R2c5OD7QKk0f4dUQ&s",
    afterImage: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBeSX_OX4jOf8H0nOMpaKwoZbCH5XW0bVpvg&s",
  },
];

const housekeepingStaff = [
  { name: "Hamza" },
  { name: "Kumail" },
  { name: "danyal" },
];

const Housekeeping = () => {
  const [tasks, setTasks] = useState(housekeepingData);
  const [selectedTask, setSelectedTask] = useState(null);

  // Update the room status (e.g., cleaning status)
  const handleUpdateStatus = (roomNumber, newStatus) => {
    setTasks(
      tasks.map((task) =>
        task.roomNumber === roomNumber ? { ...task, status: newStatus } : task
      )
    );
  };

  // Assign a new housekeeping staff to a room
  const handleAssignStaff = (roomNumber, staffName) => {
    setTasks(
      tasks.map((task) =>
        task.roomNumber === roomNumber
          ? { ...task, assignedTo: staffName }
          : task
      )
    );
  };

  return (
    <div className="p-6 space-y-6 max-w-full overflow-x-hidden">
      <h1 className="text-4xl font-semibold text-gray-800 mb-6">
        Housekeeping Management
      </h1>

      {/* Room Status List */}
      <section>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">
          Room Status
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {tasks.map((task, index) => (
            <div
              key={index}
              className="bg-white shadow-lg rounded-lg p-4 sm:p-6"
            >
              {/* Room Images (Before and After) */}
              <div className="flex space-x-4">
                <div className="w-1/2">
                  <img
                    src={task.beforeImage}
                    alt={`Room ${task.roomNumber} Before`}
                    className="w-full h-32 object-cover rounded-lg"
                  />
                  <p className="text-center mt-2 text-gray-600">Before</p>
                </div>
                <div className="w-1/2">
                  <img
                    src={task.afterImage}
                    alt={`Room ${task.roomNumber} After`}
                    className="w-full h-32 object-cover rounded-lg"
                  />
                  <p className="text-center mt-2 text-gray-600">After</p>
                </div>
              </div>

              <h3 className="text-xl font-semibold text-gray-800 mt-4">
                Room {task.roomNumber}
              </h3>
              <p className="text-gray-600 mt-2">Status: {task.status}</p>
              <p className="text-gray-600 mt-2">
                Last Cleaned: {task.lastCleaned}
              </p>
              <p className="text-gray-600 mt-2">
                Assigned To: {task.assignedTo}
              </p>
              {task.maintenanceRequired && (
                <p className="text-red-500 mt-2">Maintenance Required!</p>
              )}

              {/* Task Status Update */}
              <div className="mt-4 flex flex-wrap justify-between items-center">
                <button
                  className="bg-green-500 text-white py-2 px-4 rounded-lg"
                  onClick={() => handleUpdateStatus(task.roomNumber, "Cleaned")}
                >
                  Mark as Cleaned
                </button>
                <button
                  className="bg-yellow-500 text-white py-2 px-4 rounded-lg"
                  onClick={() =>
                    handleUpdateStatus(task.roomNumber, "In Progress")
                  }
                >
                  In Progress
                </button>
                <button
                  className="bg-gray-500 text-white py-2 px-4 rounded-lg"
                  onClick={() => handleUpdateStatus(task.roomNumber, "Pending")}
                >
                  Mark as Pending
                </button>
              </div>

              {/* Assign Staff */}
              <div className="mt-4">
                <label htmlFor="staffSelect" className="text-gray-700">
                  Assign Staff:
                </label>
                <select
                  id="staffSelect"
                  className="bg-white border rounded-lg p-2"
                  onChange={(e) =>
                    handleAssignStaff(task.roomNumber, e.target.value)
                  }
                  value={task.assignedTo}
                >
                  {housekeepingStaff.map((staff, index) => (
                    <option key={index} value={staff.name}>
                      <img
                        src={staff.photo}
                        alt={staff.name}
                        className="w-8 h-8 rounded-full inline-block mr-2"
                      />
                      {staff.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Maintenance Requests */}
      <section>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">
          Maintenance Requests
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {tasks
            .filter((task) => task.maintenanceRequired)
            .map((task, index) => (
              <div
                key={index}
                className="bg-white shadow-lg rounded-lg p-4 sm:p-6"
              >
                <h3 className="text-xl font-semibold text-gray-800 mt-4">
                  Room {task.roomNumber}
                </h3>
                <p className="text-gray-600 mt-2">Maintenance Required!</p>
                <div className="mt-4 flex flex-wrap justify-between items-center">
                  <button
                    className="bg-red-500 text-white py-2 px-4 rounded-lg"
                    onClick={() =>
                      handleUpdateStatus(task.roomNumber, "Pending")
                    }
                  >
                    Mark as Pending
                  </button>
                </div>
              </div>
            ))}
        </div>
      </section>
    </div>
  );
};

export default Housekeeping;
