import React, { useState } from "react";

// Dummy Room Data
const roomsData = [
  {
    roomNumber: "101",
    type: "Deluxe Suite",
    status: "Available",
    image: "data:image/jpeg;base64,",
  },
  {
    roomNumber: "102",
    type: "Standard Room",
    status: "Reserved",
    image: "data:image/jpeg;base64,",
  },
  {
    roomNumber: "103",
    type: "Presidential Suite",
    status: "Under Maintenance",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRGy8IZcbiL2dWwEbihlsPzH87jZS4DgxypOQ&s",
  },
  {
    roomNumber: "104",
    type: "Deluxe Suite",
    status: "Available",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRwV2a-4r9HnRIfZadzlfC68JVqD_6XlCV0jg&s",
  },
];

const RoomStatus = () => {
  const [statusFilter, setStatusFilter] = useState("All Rooms");

  // Filter rooms based on selected status
  const filteredRooms = roomsData.filter((room) => {
    if (statusFilter === "All Rooms") return true;
    return room.status === statusFilter;
  });

  // Handle status update (mock function)
  const handleStatusUpdate = (roomNumber) => {
    alert(`Status of Room ${roomNumber} updated!`);
  };

  return (
    <div className="p-6 space-y-6 max-w-full overflow-x-hidden">
      <h1 className="text-4xl font-semibold text-gray-800 mb-6">Room Status</h1>

      {/* Filter Section */}
      <section>
        <div className="flex justify-between items-center">
          <div>
            <label className="mr-4 text-lg font-semibold text-gray-700">
              Filter by Status
            </label>
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="p-2 border border-gray-300 rounded-lg"
            >
              <option>All Rooms</option>
              <option>Available</option>
              <option>Reserved</option>
              <option>Under Maintenance</option>
            </select>
          </div>
        </div>
      </section>

      {/* Room Status Section */}
      <section>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">
          Manage Room Status
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredRooms.map((room, index) => (
            <div
              key={index}
              className="bg-white shadow-lg rounded-lg p-4 sm:p-6 hover:shadow-xl transform transition-all"
            >
              {/* Room Image */}
              <img
                src={room.image}
                alt={`Room ${room.roomNumber}`}
                className="w-full h-48 object-cover rounded-lg"
              />

              <h3 className="text-xl font-semibold text-gray-800 mt-4">
                Room {room.roomNumber} - {room.type}
              </h3>
              <p
                className={`text-lg font-semibold mt-2 ${
                  room.status === "Available"
                    ? "text-green-500"
                    : room.status === "Reserved"
                    ? "text-yellow-500"
                    : "text-red-500"
                }`}
              >
                {room.status}
              </p>
              <div className="mt-4 flex justify-between items-center">
                <button
                  className="bg-blue-500 text-white py-2 px-4 rounded-lg"
                  onClick={() => handleStatusUpdate(room.roomNumber)}
                >
                  Update Status
                </button>
                <button className="bg-yellow-500 text-white py-2 px-4 rounded-lg">
                  Edit
                </button>
                <button className="bg-red-500 text-white py-2 px-4 rounded-lg">
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default RoomStatus;
